scripts.updateNginx:
echo "location ~ \"^/_int/threatconnect/download/([a-zA-Z0-9]{128})\$\" {
  proxy_set_header Host \$host;
  proxy_set_header X-Real-IP \$remote_addr;
  proxy_pass http://127.0.0.1:$(node -e "console.log(require('./config/report-proxy-server.json').port)")/download/\$1;
}" > /app/nginx/threatconnect-report-proxy.conf && systemctl restart nginx
