scripts.updateNginx:
echo "location /_threatconnect {
  proxy_set_header Host \$host;
  proxy_set_header X-Real-IP \$remote_addr;
  proxy_pass http://127.0.0.1:$(node -e "console.log(require('./config/file-server.json').port)");
}" > /app/nginx/threatconnect-file-server.conf && systemctl restart nginx
